local g_Bin2Dec = {
  New = function ( ... )
    local obj = {
      result = {},
      bSignedMagnitude = false,
      EnableSignedMagnitude = function ( ... ) 
        local this = arg[1]
        this.bSignedMagnitude = arg[2]
        end,
      translate = function ( ... )
        local function buildNUMBER ( ... )
          local compile = {
            [true] = {
              [true] = {
                ['0'] = function ( ... )
                  local obj = arg[1]
                  obj.result = obj.result + math.pow(2,obj.i)
                  return obj
                  end,
                ['1'] = function ( ... )
                  local obj = arg[1]
                  return obj
                  end,
              }, -- bFirst (true) --
              [false] = {
                ['0'] = function ( ... )
                  local obj = arg[1]
                  return obj
                  end,
                ['1'] = function ( ... )
                  local obj = arg[1]
                  obj.bFirst = true
                  obj.result = obj.result + math.pow(2,obj.i)
                  return obj
                  end,
              }, -- bFirst (false) --
            }, -- bSignedMagnitude (true) --
            [false] = {
              [true] = {
                ['0'] = function ( ... )
                  local obj = arg[1]
                  return obj
                  end,
                ['1'] = function ( ... )
                  local obj = arg[1]
                  obj.result = obj.result + math.pow(2,obj.i)
                  return obj
                  end,
              }, -- bFirst (true) --
              [false] = {
                ['0'] = function ( ... )
                  local obj = arg[1]
                  return obj
                  end,
                ['1'] = function ( ... )
                  local obj = arg[1]
                  obj.result = obj.result + math.pow(2,obj.i)
                  return obj
                  end,
              }, -- bFirst (false) --
            }, -- bSignedMagnitude (false) --
          } -- compile {} --
          local function toBitStringArray ( ... )
            local s = arg[1]
            local i = 0
            local result = {}
            local N = #arg[1]
            -- strings are left-associative but bitfields have right-associative origins--
            string.gsub(
              s,'[01]',
              function (n)             
                result[N-i] = n 
                i = i + 1  
                return n 
              end 
            ) -- gsub(s) --
            return result
          end -- toBitStringArray () --
          local function ALLPOSMAGNITUDE_BIT ( ... )
            local that = arg[1]
            local s = arg[2]
            local obj = { result=0,bFirst=false,i=0 }
            local tmp = toBitStringArray(s)
            for i,j in ipairs(tmp) do
              obj.i = i-1
              obj = compile[that.bSignedMagnitude][false][j](obj)
            end
            return obj.result
          end -- ALLPOSMAGNITUDE_BIT () --
          local function SIGNEDMAGNITUDE_BIT ( ... )
            local that = arg[1]
            local s = arg[2]
            local obj = { result=0,bFirst=false,i=0 }
            local tmp = toBitStringArray(s)
            for i,j in ipairs(tmp) do
              -- MSB holds sign bit (dont include in tally) --
              obj.i = i-1
              obj = compile[that.bSignedMagnitude][obj.bFirst][j](obj) -- print(obj.bFirst)
            end
            return qtest(that.bSignedMagnitude,-1,1)*obj.result
          end -- SIGNEDMAGNITUDE_BIT () --
          local that = arg[1]
          local s = arg[2]
          local result = 0
          string.gsub(
            s,'^[01]', -- match 0 or 1 at MSB --
            function (n)
              result = qtest(n=='1',SIGNEDMAGNITUDE_BIT(that,s),ALLPOSMAGNITUDE_BIT(that,s))
              return n
            end
          ) -- gsub(s) --
          return result
        end -- buildNUMBER () --
        local this = arg[1]
        local result = {}
        local N = #arg
        for i=2,N do -- dont include self --
          result[i-1] = buildNUMBER(this,arg[i])
        end -- loopf(i) --
        this.result = result
        return result
        end,
      show = function ( ... )
        local this = arg[1]
        print ( 'result = {' )
        for i,j in ipairs(this.result) do
          print ( '['..i..']='..j..',' )
        end -- loops(i,j) --
        print ( '} result {} --' )
        end,
    } -- obj {} --
    return obj
    end,
} -- g_Bin2Dec {} --

