local g_ToBinary = {
  New = function ( ... )
    local obj = {
      bitstringResult = {},
      bitStringResolution = 32,
      toBinary = function ( ... ) 
        local this = arg[1]
        local args = #arg
        local x64res = this.bitStringResolution
        local function padZero ( ... )
          local n      = arg [ 1 ]
          local neg    = arg [ 2 ]
          local padnum = 32 - #arg [ 1 ]
          if x64res then
            padnum = x64res-#arg[1]
          elseif system.x86_64 then
            padnum = padnum + 32 -- 64bit platform (when available) --
          end
          local padstr = '0'
          if neg then
            padstr = '1'
          end
          for i=1,padnum do
            n = padstr..n
          end
          return n
        end -- padZero () --
        local function buildBITSTRING ( ... )
          local str = '' -- iff value is positive, there should be a zero ('0') in the MSB
          local num = 0
          local num_n = tonumber( arg [ 1 ] )
          local bFirstFind = false
          local neg = false 
          if num_n < 0 then
            neg = true
          end
          num = math.abs ( num_n )
          while ( num >= 1 ) do    
            local r = num 
            if neg and bFirstFind then -- confirmed 2's complement --
              if ( r%2 == 0 ) then
                str = '1'..str
              else
                str = '0'..str
              end
            else -- positive number (so far) --
              if ( r%2 == 0 ) then
                str = '0'..str
              else
                str = '1'..str
                bFirstFind = true -- marked for 2's complement --
              end
            end -- loopf --
            local temp = r / 2
            num = temp - temp%1
          end -- loopf --
          return padZero ( str,neg )
        end -- buildBITSTRING () --
        for i=2,args do
