    if ( r.score-s > g_minNPGAIN ) then
      print ( 'Further improvement found..' )
      print ( "Score: "..r.score.." | mode: "..winningQSTAB_by_name[r.mode].." | QS"..r.temp )  
      print ( "Gain: "..r.score.." +"..r.score - s ) 
